import React from "react";
import { styled } from "~/stitches.config";
import styledComp from "styled-components";

const Title = styled("h1", {
  color: "green",
  backgroundColor: "yellow",
  ":hover": {
    color: "red",
  },
});

const SubTitle = styled("h2", {
  color: "green",
});

const BaseButton = styled("button", {
  outline: "none",
  border: "none",
});

const Button = styled(BaseButton, {
  padding: "0.5rem 1rem",
});

const ConstructButton = styled(Button, {
  backgroundColor: "green",
  color: "white",
});

const DestructButton = styled(Button, {
  backgroundColor: "red",
  color: "white",
  variants: {
    shadow: {
      near: {
        boxShadow: "4px 4px 4px #00000040",
      },
    },
  },
});

const ButtonGroup = styled("div", {
  display: "flex",
  flexDirection: "row",
  gap: "1rem",
  [`> ${DestructButton}`]: {
    order: -1,
  },
  [`> ${ConstructButton}`]: {
    order: 1,
  },
});

export default function Main() {
  return (
    <>
      <Title>Hello world</Title>
      <SubTitle>Just testing</SubTitle>
      <ButtonGroup>
        <DestructButton shadow="near">Delete</DestructButton>
        <Button>Cancel</Button>
        <ConstructButton>Create</ConstructButton>
      </ButtonGroup>
    </>
  );
}
