import { createStyled } from "@stitches/react";

export const { styled, css } = createStyled({
  breakpoints: {},
  tokens: {
    colors: {
      $primary: "navy",
      $secondary: "blue",
      $background: "white",
      $textColor: "black",
      $destructive: "#9b1010",
      $constructive: "#178917",
    },
    space: {
      $none: "0",
      $s: "0.25rem",
      $m: "0.5rem",
      $l: "0.75rem",
    },
  },
  breakpoints: {
    mobile: (rule) => `@media (max-width:500px) {${rule}}`,
    tablet: (rule) => `@media (max-width:1024px)`,
  },
  utils: {},
});
