import React from "react";
import { styled, css } from "~/stitches.config";
import styledComp from "styled-components";

const darkTheme = css.theme({
  $background: "#303030",
  $textColor: "#f0f0f0",
});

const Title = styled("h1", {
  color: "$constructive",
  backgroundColor: "yellow",
  ":hover": {
    color: "$destructive",
  },
});

const SubTitle = styled("h2", {
  color: "$constructive",
});

const BaseButton = styled("button", {
  outline: "none",
  border: "none",
  cursor: "pointer",

  backgroundColor: "$background",
  color: "$textColor",
});

const Button = styled(BaseButton, {
  padding: "$s $s",
  tablet: {
    padding: "$s $m",
  },
  desktop: {
    padding: "$m $l",
  },
  print: {
    padding: "4rem",
  },
});

const ConstructButton = styled(Button, {
  backgroundColor: "$constructive",
  color: "white",
});

const DestructButton = styled(Button, {
  backgroundColor: "$destructive",
  color: "white",
});

const ButtonGroup = styled("div", {
  display: "flex",
  flexDirection: "row",
  gap: "1rem",
  [`> ${DestructButton}`]: {
    order: -1,
  },
  [`> ${ConstructButton}`]: {
    order: 1,
  },
});

export default function Main() {
  return (
    <>
      <Title>Hello world</Title>
      <SubTitle>Just testing</SubTitle>
      <ButtonGroup className={darkTheme}>
        <DestructButton css={{}}>Delete</DestructButton>
        <Button>Cancel</Button>
        <ConstructButton>Create</ConstructButton>
      </ButtonGroup>
    </>
  );
}
